import React from 'react';
import { useEffect } from 'react';
import { Routes, Route, useNavigate, useLocation, Navigate } from 'react-router-dom';

// Import your remaining page components
import AboutPage from './pages/AboutPage';
import TranslatorPage from './pages/TranslatorPage';

import './App.css';

function App() {
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    // This logic still forces a reload to the '/about' page, as requested
    if (location.pathname !== '/about') {
        navigate('/about');
    }
  }, []); // Empty array ensures this runs only on initial mount

  return (
    <Routes>
      {/* This route now redirects the root path "/" to "/about".
        So if a user ever lands on your site's base URL, they'll be sent to the About page.
      */}
      <Route path="/" element={<Navigate to="/about" />} />

      <Route path="/about" element={<AboutPage />} />
      <Route path="/translator" element={<TranslatorPage />} />
    </Routes>
  );
}

export default App;
