import React, { useState } from 'react';
import './App.css';
import Recorder from './Components/Recorder';
import { transcribeAudio, translateText, textToSpeech } from './api/api';

function App() {
  const [transcript, setTranscript] = useState('');
  const [translatedText, setTranslatedText] = useState('');
  const [audioUrl, setAudioUrl] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');

  const handleRecordingStop = async (audioBlob) => {
    setIsLoading(true);
    setError('');
    setTranscript('');
    setTranslatedText('');
    setAudioUrl('');

    try {
      // Step 1: Transcribe Audio
      const transcribedText = await transcribeAudio(audioBlob);
      setTranscript(transcribedText);

      // Step 2: Translate Text
      const translated = await translateText(transcribedText, 'es');
      setTranslatedText(translated);

      // Step 3: Convert Translated Text to Speech
      const finalAudioUrl = await textToSpeech(translated, 'es');
      setAudioUrl(finalAudioUrl);

    } catch (err) {
      setError('An error occurred. Please try again.');
      console.error(err);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Silent Disco Translator ðŸŽ§</h1>
        <p>Record your voice and get it translated into Spanish.</p>
        <Recorder onStop={handleRecordingStop} disabled={isLoading} />
        {isLoading && <p className="loading">Processing...</p>}
        {error && <p className="error">{error}</p>}

        <div className="results">
          {transcript && (
            <div className="result-item">
              <h3>Transcript:</h3>
              <p>"{transcript}"</p>
            </div>
          )}
          {translatedText && (
            <div className="result-item">
              <h3>Translated Text:</h3>
              <p>"{translatedText}"</p>
            </div>
          )}
          {audioUrl && (
            <div className="result-item">
              <h3>Listen:</h3>
              <audio controls autoPlay src={audioUrl}>
                Your browser does not support the audio element.
              </audio>
            </div>
          )}
        </div>
      </header>
    </div>
  );
}

export default App;
